import axios from "axios";



export default function API(){
    const instance = axios.create({
        baseURL:"https://jsonplaceholder.typicode.com"
    });

    function GetData(){
        var data;
        instance.get("/posts").then((respose)=>{
            console.log(response.data);
            data = response.data;
        }).catch((error)=>{
            console.log(error);
        });

        return data;
    }

    function CreatePost(arr){
        var status;
        instance.post("/post",arr).then((response)=>{
            console.log(response);
            status = response.status;
        }).catch((error)=>console.log(error));

        return status;
    }

    function UpdatePost(arr){
        var status;
        instance.put("/post",arr).then((response)=>{status = response.status;}).catch((error)=>{console.log(error)});
        return status;
    }

    function DeleteData(personalId){
        var status;
        instance.delete("post",personalId).then((response)=>{status = response.status}).catch(console.log(error));
    }


}
